            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
    }

    const shuffledEmotions = shuffleArray(emotions);
    const feelingsContainer = document.getElementById("feelings-container");

